// SPDX-License-Identifier: MIT
pragma solidity ^0.6.0;

/*
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/AccessControl.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/payment/PullPayment.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/utils/SafeCast.sol";
import "https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/math/SafeMath.sol";
*/
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/payment/PullPayment.sol";
import "@openzeppelin/contracts/utils/SafeCast.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./DateTime.sol";


contract BFactor is Ownable, AccessControl, PullPayment {

    string public name;
    bytes32 public constant BUSINESS_ROLE = keccak256("BUSINESS_ROLE");
    bytes32 public constant DEBTOR_ROLE = keccak256("DEBTOR_ROLE");
    bytes32 public constant FACTOR_ROLE = keccak256("FACTOR_ROLE");
    DateTime dateTime;

    struct Business {
        uint Id;
        string name;
        address account;
        string addressOfBusiness;
        string telephone;
        string email;
    }

    struct Debtor {
        uint Id;
        string name;
        address account;
        string addressOfDebtor;
        string telephone;
        string email;

    }

    struct Factor {
        uint Id;
        string name;
        address account;
        string addressOfFactor;
        string telephone;
        string email;

    }

    enum InvoiceState { Created, Pending, Acknowledged, Offered, Factorable, Factored, Settled }
    enum OfferState { Pending, Accepted }

    struct Invoice {
        uint invoiceId;
        address payable owner;
        address issuedTo;
        uint amount;
        address paidTo;
        InvoiceState state;
        uint invoiceDate;
    }

    struct Offer {
        uint invoiceId;
        uint advanceRate;
        uint discountRate;
        OfferState state;
        address payable owner;
    }

    /** Committment Scheme
     */
    struct Commit {
        bytes32 commit;
        uint64 block;
        bool revealed;
        bool confirmed;
        address payable owner;
        address debtor;
        uint amount;
        uint invoiceID;
    }

    mapping (uint => Commit) public commits;

    mapping(address => Business) public businesses;
    mapping(uint => Debtor) public debtors;
    mapping(address => Factor) public factors;
    mapping(uint => Invoice) public invoices;
    mapping(uint256 => Offer) public offers;

    uint public businessesCount;
    uint public debtorsCount;
    uint public factorsCount;
    uint public invoicesCount;
    uint public offersCount;
    uint public commitsCount;

    /* Events */
    event InvoiceStateChanged(uint _invoiceId, InvoiceState _newState);

    event InvoiceRejected(uint _invoiceId, string _reason);

    event OfferCreated(uint _offerId);

    event OfferAccepted(uint _offerId);

    event OfferRejected(uint _offerId, string _reason);

    event AdvanceRateDeposited(uint _offerId, int256 _amount);

    event InvoiceSettled(uint _invoiceId, int256 _amount);

    event RemainingDeposited(uint _invoiceId, int256 _amount);

    /* Modifiers */
    modifier onlyBusiness {
        require(
            hasRole(BUSINESS_ROLE, msg.sender),
            "Only business can call this function."
        );
        _;
    }

    modifier onlyDebtor {
        require(
            hasRole(DEBTOR_ROLE, msg.sender),
            "Only debtor can call this function."
        );
        _;
    }

    modifier onlyFactor {
        require(
            hasRole(FACTOR_ROLE, msg.sender),
            "Only factor can call this function."
        );
        _;
    }

    //Should be a payable constructor
    constructor(string memory _name) public payable {
        name = _name;
        businessesCount = 0;
        debtorsCount = 0;
        factorsCount = 0;
        invoicesCount = 0;
        offersCount = 0;
        commitsCount = 0;

        dateTime = new DateTime();
    }

    function createBusiness(string memory _name, address _account, string memory _addressOfBusiness, string memory _telephone, string memory _email) public onlyOwner {
        businessesCount ++;
        registerBusiness(Business(businessesCount, _name, _account, _addressOfBusiness, _telephone, _email));
    }

    function registerBusiness(Business memory _business) internal {
        _setupRole(BUSINESS_ROLE, _business.account);
        businesses[_business.account] = _business;
    }

    function createDebtor(string memory _name, address _account,string memory _addressOfDebtor, string memory _telephone, string memory _email) public onlyOwner {
        debtorsCount ++;
        registerDebtor(Debtor(debtorsCount, _name, _account, _addressOfDebtor, _telephone, _email));
    }

    function registerDebtor(Debtor memory _debtor) internal {
        _setupRole(DEBTOR_ROLE, _debtor.account);
        debtors[_debtor.Id] = _debtor;
    }

    function createFactor(string memory _name, address _account, string memory _addressOfFactor, string memory _telephone, string memory _email) public onlyOwner {
        factorsCount ++;
        registerFactor(Factor(factorsCount, _name, _account, _addressOfFactor, _telephone, _email));
    }

    function registerFactor(Factor memory _factor) internal {
        _setupRole(FACTOR_ROLE, _factor.account);
        factors[_factor.account] = _factor;
    }

    /**

    Committment Scheme
    ==================

    1. Business to get invoice data hash using business address, debtors address, and amount 
    2. Calculate a random number for the invoice ID
    3. Commit the hash and invoice ID to the chain and notify the Debtor to acknowledge
    4. Debtor use own data to calculate the invoice hash
    5. Acknlowedge the invoice by comparing the invoice hash in the commit to the calcualte hash (if hash is differnt it should affect the Business rating)
    6. If hash is correct set confirmed to true and only then Business can reveal the invoice (i.e. create it)
    7. Create invoice and set the invoice ID to the same ID in the commit and set revealed to true
    8. Set the invoice status to Acknowledged

    */

    function getInvoiceHash(string memory _invoiceData) view public returns(bytes32 hashedInvoiceData){
        return getHashFromString(_invoiceData);
    }

    function getInvoiceDateTimeStamp(uint16 _invoiceYear, uint8 _invoiceMonth, uint8 _invoiceDay) public view returns(uint) {
        return dateTime.toTimestamp(_invoiceYear, _invoiceMonth, _invoiceDay);
    }

    function getInvoiceDataEncoded(address _invIssuer, address _invDebtor, uint _invAmount, uint _invDate) public view returns (bytes memory) {
        return abi.encodePacked(_invIssuer, _invDebtor, _invAmount, _invDate);
    }

    function createInvoiceId() view public onlyBusiness returns(uint) {
        uint invoiceId = random();

        return invoiceId;
    } 

    function commit(address payable _owner, address _debtor, uint _amount, bytes32 _dataHash, uint _invoiceId) public {
        commits[commitsCount].commit = _dataHash;
        commits[commitsCount].block = uint64(block.number);
        commits[commitsCount].revealed = false;
        commits[commitsCount].confirmed = false;
        commits[commitsCount].owner = _owner;
        commits[commitsCount].invoiceID = _invoiceId;
        commits[commitsCount].debtor = _debtor;
        commits[commitsCount].amount = _amount;
        commitsCount ++;
        emit CommitHash(_owner,commits[_invoiceId].commit,commits[_invoiceId].block, _invoiceId);
    }
    event CommitHash(address sender, bytes32 dataHash, uint64 block, uint invoiceId);

    function acknowledgeInvoice(bytes32 _hashedInvoiceData, uint _invoiceId) public onlyDebtor {        
        require(commits[_invoiceId].commit == _hashedInvoiceData, "Debtor calculated hash must be equal to the hash in commit.");

        commits[_invoiceId].confirmed=true;

        emit ConfirmHash(msg.sender, _hashedInvoiceData, _invoiceId);
    }
    event ConfirmHash(address debtor, bytes32 dataHash, uint invoiceId);

    function createInvoice(uint _invoiceId, uint _commitID, uint16 _invoiceYear, uint8 _invoiceMonth, uint8 _invoiceDay) public {
        //require(commits[_commitID].confirmed == true, "CommitReveal: the hash has been confirmed by the debtor");
        commits[_commitID].revealed=true;

        invoicesCount ++;
        uint invoiceDate = dateTime.toTimestamp( _invoiceYear, _invoiceMonth, _invoiceDay);

        invoices[invoicesCount].invoiceId = _invoiceId;
        invoices[invoicesCount].owner = commits[_commitID].owner;
        invoices[invoicesCount].issuedTo = commits[_commitID].debtor;
        invoices[invoicesCount].amount = commits[_commitID].amount;
        invoices[invoicesCount].paidTo = commits[_commitID].owner;
        invoices[invoicesCount].state = InvoiceState.Acknowledged;
        invoices[invoicesCount].invoiceDate = invoiceDate;

        emit InvoiceStateChanged(_invoiceId, InvoiceState.Acknowledged);
    }

    // + offerInvoice(): void
    function offerInvoice(uint _invoiceId) public onlyBusiness {
        require(msg.sender == invoices[_invoiceId].owner, "This method can only be invoked by invoice owner");
        require(invoices[_invoiceId].state == InvoiceState.Acknowledged, "The method can only offer an acknowledged invoice");
        invoices[_invoiceId].state = InvoiceState.Offered;
        emit InvoiceStateChanged(_invoiceId, InvoiceState.Offered);
    }

    // + reviewOfferedInvoice(): bool
    // function reviewInvoice(uint _invoiceId) public onlyFactor {
    //     require(invoices[_invoiceId].state == InvoiceState.Offered, "The method can only handle an offered invoice");
    //     //Todo: Add invoice review process
    //     bool reviewResult = false;

    //     if(reviewResult) {
    //         acceptInvoice(_invoiceId);
    //     } else {
    //         rejectInvoice(_invoiceId);
    //     }
    // }

    function acceptInvoice(uint _invoiceId) public onlyFactor {
        require(invoices[_invoiceId].state == InvoiceState.Offered, "The method can only handle an offered invoice");
        invoices[_invoiceId].state = InvoiceState.Factorable;
        emit InvoiceStateChanged(_invoiceId, InvoiceState.Factorable);
    }

    function rejectInvoice(uint _invoiceId, string memory _reason) public onlyFactor {
        require(invoices[_invoiceId].state == InvoiceState.Offered, "The method can only handle an offered invoice");
        invoices[_invoiceId].state = InvoiceState.Acknowledged;
        emit InvoiceStateChanged(_invoiceId, InvoiceState.Acknowledged);
        emit InvoiceRejected(_invoiceId, _reason);
    }

    // + createOffer(): void
    function createOffer(uint _invoiceId, uint _advanceRate,uint _discountRate) public onlyFactor {
        //require(invoices[_invoiceId].state == InvoiceState.Factorable, "The method can only handle a factorable invoice");
        offersCount ++;
        offers[offersCount] = Offer(_invoiceId, _advanceRate, _discountRate, OfferState.Pending, msg.sender);
        emit OfferCreated(offersCount);
    }

    // + reviewOffer(): bool
    // function reviewOffer(uint _offerId) public onlyBusiness {
    //     require(offers[_offerId].state == OfferState.Created, "The method can only handle a new offer");

    //     //Todo: Add offer review process
    //     bool reviewResult = false;
    //     string rejectionReason = "";

    //     if(reviewResult) {
    //         acceptOffer(_offerId);
    //     } else {
    //         rejectOffer(_offerId, rejectionReason);
    //     }
    // }

    function acceptOffer(uint _offerId) public onlyBusiness {
        require(offers[_offerId].state == OfferState.Pending, "The method can only handle a new offer");
        offers[_offerId].state = OfferState.Accepted;
        emit OfferAccepted(_offerId);
        uint invId=0;
        for(uint i=1; i<=invoicesCount; i++){
            if(offers[_offerId].invoiceId == invoices[i].invoiceId){
                invId=i;
            }
        }
        invoices[invId].state = InvoiceState.Factored;
        emit InvoiceStateChanged(offers[_offerId].invoiceId, InvoiceState.Factored);
    }

    function rejectOffer(uint _offerId, string memory _reason) public onlyBusiness {
        require(offers[_offerId].state == OfferState.Pending, "The method can only handle a new offer");
        emit OfferRejected(_offerId, _reason);
    }

    // + sendAdvanceRate(): void
    function sendAdvanceRate(uint _invoiceId) public payable onlyFactor {
        require(invoices[_invoiceId].state == InvoiceState.Factored,
        "The method can only handle a factored invoice");

        uint256 offerId = getOfferByInvoiceId(_invoiceId);
        require(offers[offerId].owner == msg.sender, "The method can only be called by the owner of the offer");

        //uint256 advanceRateAmount = SafeMath.mul(invoices[_invoiceId].amount, SafeMath.div(offers[offerId].advanceRate,100));
        int256 advanceRateAmount = SafeCast.toInt256((invoices[_invoiceId].amount * offers[offerId].advanceRate) / 100);
        emit AdvanceRateDeposited(offerId, advanceRateAmount);
        _asyncTransfer(invoices[_invoiceId].owner, /*SafeCast.toUint256(advanceRateAmount)*/1);
        msg.sender.transfer(SafeCast.toUint256(advanceRateAmount));
    }

    function withdrawAdvanceRate(uint _invoiceId) public onlyBusiness {
        require(msg.sender == invoices[_invoiceId].owner, "This method can only be invoked by invoice owner");
        uint256 offerId = getOfferByInvoiceId(_invoiceId);
        withdrawPayments(offers[offerId].owner);
    }

    function getOfferByInvoiceId(uint _invoiceId) public view returns(uint256) {
        uint256 _result = 0;

        for(uint i = 1; i < offersCount+1; i++) {
            if(offers[i].invoiceId == _invoiceId) {
                _result = i;
                break;
            }
        }

        return _result;
    }

    // + settleInvoice(): void
    function settleInvoice(uint _invoiceId) public onlyDebtor {
        require(invoices[_invoiceId].state == InvoiceState.Factored,
        "The method can only handle a factored invoice");

        int256 invoiceAmount = SafeCast.toInt256(invoices[_invoiceId].amount);
        _asyncTransfer(invoices[_invoiceId].owner, SafeCast.toUint256(invoiceAmount));
        emit InvoiceSettled(_invoiceId, invoiceAmount);
        invoices[_invoiceId].state = InvoiceState.Settled;
    }

    // + sendRemaining(): void
    function sendRemaining(uint _invoiceId) public payable {
        //just to pass this check - TBR
        invoices[_invoiceId].state = InvoiceState.Settled;

        require(invoices[_invoiceId].state == InvoiceState.Settled,
        "The method can only handle a settled invoice");

        uint256 offerId = getOfferByInvoiceId(_invoiceId);
        require(offers[offerId].owner == msg.sender, "The method can only be called by the owner of the offer");

        int256 advanceRateAmount = SafeCast.toInt256((invoices[_invoiceId].amount * offers[offerId].advanceRate) / 100);
        int256 discountRateAmount = SafeCast.toInt256((invoices[_invoiceId].amount * offers[offerId].discountRate) / 100);
        int256 remainingAmount = SafeCast.toInt256(invoices[_invoiceId].amount) - advanceRateAmount - discountRateAmount;
        _asyncTransfer(invoices[_invoiceId].owner, SafeCast.toUint256(remainingAmount));

        emit RemainingDeposited(_invoiceId, remainingAmount);
    }

    function getCount() public view returns (uint, uint){
        return (invoicesCount,offersCount);
    }


    /**
    Utils
     */
    function random() private view returns (uint8) {
       return uint8(uint256(keccak256(abi.encodePacked(block.timestamp, block.difficulty)))%251);
    }

   function getHash(bytes32 data) public view returns(bytes32){
        return keccak256(abi.encodePacked(address(this), data));
    }

    function getHashFromString(string memory data) public view returns(bytes32) {
        return keccak256(abi.encodePacked(address(this), stringToBytes32(data)));
    }

    function stringToBytes32(string memory source) public pure returns (bytes32 result) {
        bytes memory tempEmptyStringTest = bytes(source);
        if (tempEmptyStringTest.length == 0) {
            return 0x0;
        }

        assembly {
            result := mload(add(source, 32))
        }
    }


    function getCommitsCount() public view returns (uint){
        return commitsCount;
    }

    function getCommitOwner(uint _id) public view returns (address) {
        return commits[_id].owner;
    }

    function getDebtorName(uint _id) public view returns (string memory) {
        return debtors[_id].name;
    }

    function getDebtorNameByAddress(address _debtorAddress) public view returns (string memory) {
        for(uint i = 0 ; i <= debtorsCount; i++) {
            Debtor memory debtor = debtors[i];
            if(debtor.account == _debtorAddress) {
                return debtor.name;
            }
        }
    }

    function getDebtorAddress(uint _id) public view returns (address) {
        return debtors[_id].account;
    }

    function getInvoiceAmount(uint _invoiceID) public view returns (uint) {
        return invoices[_invoiceID].amount;
    }

    function getInvoiceOwner(uint _invoiceID) public view returns (address) {
        return invoices[_invoiceID].owner;
    }
}
